document.addEventListener('DOMContentLoaded',function(){var e=document.getElementById("card-container");for(var b=0; b<repositories.length; b++){card=document.createElement('div');card.classList.add("d-flex",'flex-column','pcard');header=document.createElement('div');header.classList.add("d-flex",'flex-row','pcard-header');he=document.createElement('h3');he.innerText=repositories[b]['name'].split('/')[1];a=document.createElement('a');a.classList.add('nav-item','nav-link');a.href=repositories[b]['url'];a.setAttribute('target','_blank');ad=document.createElement('div');ad.classList.add('d-flex');ic=document.createElement('i');ic.classList.add('bi','bi-github','ico-text');ad.appendChild(ic);ad.innerHTML+="Github";a.appendChild(ad);header.appendChild(he);header.appendChild(a);p=document.createElement('p');p.innerText=repositories[b]['description'];dt=document.createElement('div');dt.classList.add('d-flex','flex-row','pcard-tags');for(var c=0; c<repositories[b]['languages'].length; c++){sp=document.createElement('span');sp.innerText=repositories[b]['languages'][c];dt.appendChild(sp);}card.appendChild(header);card.appendChild(p);card.appendChild(dt);card.style.opacity=0;e.appendChild(card);}d(0);function d(a){$(e.children[a]).fadeTo(500,1,function(){d(a+1);});}});